statement: SET statement_timeout='58s'; SET idle_session_timeout='58s';CREATE OR REPLACE FUNCTION pg_temp.count_estimate(
    query text
) RETURNS integer LANGUAGE plpgsql AS $$
DECLARE
    plan jsonb;
BEGIN
    EXECUTE 'EXPLAIN (FORMAT JSON)' || query INTO plan;
    RETURN plan->0->'Plan'->'Plan Rows';
END;
$$;

with approximation as (
    select reltuples as estimate
    from pg_class
    where oid = 17583
)
select 
  case 
    when estimate = -1 then (select pg_temp.count_estimate('select * from public.completed_check_ins;'))
    when estimate > 50000 then estimate
    else (select count(*) from public.completed_check_ins)
  end as count,
  estimate = -1 or estimate > 50000 as is_estimate
from approximation;

